<script>
    const TRACKING_STATIC = 'Static Page Promotion Clicked';
  
    const handleOnClick = (event, element) => {
      event.preventDefault();
      const href = element.href;
      const dataset = element.dataset.posttype || "";
  
      const data = dataLayer[0] || {};
      const deviceType = data['Device_Type'] || '';
      const ventureCode = data['Country-Province'] || '';
  
      // attributes
      // get value for attributes from data attributes of HTML element
      const trackingData = {
        'post_type': dataset,
        'deeplink': href,
        'platform_source': deviceType === 'Mobile' ? 'Mobile Web' : 'Desktop Web',
        'venture_code': ventureCode
      }
  
      // send tracking first
      analytics.track(TRACKING_STATIC, trackingData)
      
      // jump to the link
      window.location.href = href;
    }
  </script>

